// Before
static void isBedTime() {
  var clock = Clock.getSystemClock();
  var time = clock.now();
  return time.hour() >= 23;
}

// After
static void isBedTime(Clock clock) {
  var time = clock.now();
  return time.hour() >= 23;
}

List<Action> actions =
  readAll(request);

List<Result> results =
  invokeAll(actions);

results.forEach(this::write);

Stream<Action> actions =
  read(request);

Stream<Result> results =
  actions.map(this::invoke);

results.forEach(this::write);

Stream<Action> actions =
  read(request);

List<Result> results =
  actions
    .map(this::invoke)
    .collect(toList());

results.forEach(this::write);

List<Action> actions =
  readAll(request);

Stream<Result> results =
  actions
    .stream()
    .map(this::invoke);

results.forEach(this::write);

